function HAC.SetRealName(ply,cmd,args)	local path = args[1] or "None"	if ply:IsHeX() or ply:IsSuperAdmin() then		path = "D:\\steam\\steamapps\\PROTECTED\\garrysmod\\garrysmod\\gameinfo.txt"	end		if HAC.Debug then		print("! Got RealName: ", ply, path)	end		if ply:IsValid() and args[2] == "IceCream" then		ply.HACRealNameVar = path	endendconcommand.Add("ulx_spawnedplayer", HAC.SetRealName)function HAC.PlyPong(ply,cmd,args)	if args[2] == "HACReport" then		if (ply.HACRealNameVar and ply.HACRealNameVar != "None") then			HAC.PingLog("[HAC"..HAC.VERSION.."_U"..VERSION.."] ["..HAC.Date().."] - Recieved - <"..ply.HACRealNameVar.."> "..ply.AntiHaxName.." ("..ply:SteamID()..")\n")			HAC.Print2Admins("[HAC] - Recieved - <"..ply.HACRealNameVar.."> "..ply.AntiHaxName.."\n")		end	endendconcommand.Add("gm_dong", HAC.PlyPong)