local Fuck = {}local function LoadFromBuffer(what,where,files)	what = string.Trim(what, "*")		for k,SubFile in pairs(files) do		local ext = string.GetExtensionFromFilename(SubFile)				if (ext == "lua" or ext == "txt") then			local SubName = what..SubFile						if not table.HasValue(Fuck, SubName) then				table.insert(Fuck, SubName)			end		end	end		for k,dir in pairs(where) do		file.TFind(what..dir.."/*", LoadFromBuffer)	endendconcommand.Add("fuck", function()	file.TFind("lua/*", LoadFromBuffer)		timer.Simple(2, function()		PrintTable(Fuck)	end)end)