if not hac then	if file.Exists("lua/includes/modules/gmsv_hac.dll",true) then		if not HACGlobalNoReload then			HACGlobalNoReload = true			require("hac")						local Full = util.RelativePathToFull						hac.OldMKDIR	= hac.MKDIR			hac.OldDelete	= hac.Delete			hac.OldCopy		= hac.Copy			hac.OldWrite	= hac.Write						local Drives = {				["c:"] = true, ["d:"] = true, ["e:"] = true, ["f:"] = true, ["g:"] = true,				["h:"] = true, ["i:"] = true, ["j:"] = true, ["k:"] = true, ["l:"] = true,				["m:"] = true, ["n:"] = true, ["o:"] = true, ["p:"] = true, ["q:"] = true,				["r:"] = true, ["s:"] = true, ["t:"] = true, ["u:"] = true, ["v:"] = true,				["w:"] = true, ["x:"] = true, ["y:"] = true, ["z:"] = true,			}			function hac.MKDIR(path)				if path:sub(-4):find(".") then --Only works for 3 char file extensions!					path = string.GetPathFromFilename(path):Trim("/")				end								if hac.IsDir(path) then					return true				end								local Tab = string.Explode("/", path)								local new = ""				for k,v in ipairs(Tab) do					new = new.."/"..v					new = new:Trim("/")										if not Drives[ v:lower() ] and not hac.IsDir( Full(new) ) then --Messy!						hac.OldMKDIR( Full(new) )					end				end			end						function hac.Delete(path)				if not hac.Exists(path) then					ErrorNoHalt("hac.Delete failed, '"..path.."' is gone?!\n", 2)					return false				end								return hac.OldDelete(path)			end						function hac.Copy(old,new)				if not hac.IsDir(new) then					hac.MKDIR(new)				end								return hac.OldCopy(old,new)			end						function hac.Move(old,new)				hac.Copy(old,new)								hac.Delete(old)			end						function hac.Write(path,str)				if not hac.IsDir(path) then					hac.MKDIR(path)				end								return hac.OldWrite(path, str)			end		end		Msg("   OK! gmsv_hac\n")	else		Msg("   DAMN! gmsv_hac gone!\n")	endelse	Msg("   DAMN! hac\n")	Msg("   already loaded dumbass!\n")endif not gatekeeper then	if file.Exists("lua/includes/modules/gmsv_gatekeeper.dll", true) then		require("gatekeeper")		Msg("   OK! gmsv_gatekeeper\n")	else		Msg("   DAMN! Gatekeeper gone!\n")	endelse	Msg("   DAMN! Gatekeeper exists\n")endif not (package.loaded.rcon) then	if file.Exists("lua/includes/modules/gmsv_rcon.dll", true) then		require("rcon")		Msg("   OK! gmsv_rcon\n")	else		Msg("   DAMN! RCON gone!\n")	endelse	Msg("   DAMN! RCON exists\n")endif not cvar2 then	if file.Exists("lua/includes/modules/gm_cvar2.dll", true) then		require("cvar2")		Msg("   OK! gm_cvar2\n")	else		Msg("   DAMN! CVar2 gone!\n")	endelse	Msg("   DAMN! CVar2 exists\n")endif not (package.loaded.slog) then	if file.Exists("lua/includes/modules/gm_slog.dll", true) then		require("slog")		Msg("   OK! gm_slog\n")	else		Msg("   DAMN! SLog gone!\n")	endelse	Msg("   DAMN! SLog exists\n")end